import './App.css';
import React, { useReducer } from 'react';
import ClientDataWindow from './ClientDataWindow';
import VehicleDataWindow from './VehicleDataWindow';
import ServiceSelectionWindow from './ServiceSelectionWindow';
import WorkOrderWindow from './WorkOrderWindow';

// Create a new context
export const FormContext = React.createContext();

// Initial form state
const initialState = {
  clientName: '',
  email: '',
  contactNumber: '',
  taxId: '',
  identificationType: '',
  brand: '',
  model: '',
  licensePlate: '',
  fuelLevel: '',
  vehicleCondition: '',
  selectedServices: [],
};

// Reducer function to update the form state
const formReducer = (state, action) => {
  switch (action.type) {
    case 'UPDATE_FIELD':
      return { ...state, [action.field]: action.value };
    case 'TOGGLE_SERVICE':
      const { service } = action;
      if (state.selectedServices.includes(service)) {
        return {
          ...state,
          selectedServices: state.selectedServices.filter(s => s !== service),
        };
      } else {
        return {
          ...state,
          selectedServices: [...state.selectedServices, service],
        };
      }
    default:
      return state;
  }
};

const App = () => {
  const [state, dispatch] = useReducer(formReducer, initialState);

  return (
    <div>
      <h1>Propuesta - Car Shop</h1>
      <FormContext.Provider value={[state, dispatch]}>
        <ClientDataWindow />
        <VehicleDataWindow />
        <ServiceSelectionWindow />
        <WorkOrderWindow />
      </FormContext.Provider>
    </div>
  );
};

export default App;
