import React, { useContext, useState } from 'react';
import { FormContext } from './App';

const ServiceSelectionWindow = () => {
  const [state, dispatch] = useContext(FormContext);
  const [customService, setCustomService] = useState('');

  const handleServiceToggle = (e) => {
    const { value, checked } = e.target;
    dispatch({ type: 'TOGGLE_SERVICE', service: value });
  };

  const handleCustomServiceChange = (e) => {
    setCustomService(e.target.value);
  };

  const handleAddCustomService = () => {
    if (customService.trim() !== '') {
      dispatch({ type: 'ADD_SERVICE', service: customService });
      setCustomService('');
    }
  };

  return (
    <div>
      <h2>Menú de servicios</h2>
      <div className="form-group">
        <label>
          <input
            type="checkbox"
            name="selectedServices"
            value="cambioAceite"
            checked={state.selectedServices.includes('cambioAceite')}
            onChange={handleServiceToggle}
            className="form-check-input"
          />
          Cambio de aceite
        </label>
      </div>
      <div className="form-group">
        <label>
          <input
            type="checkbox"
            name="selectedServices"
            value="cambioFrenos"
            checked={state.selectedServices.includes('cambioFrenos')}
            onChange={handleServiceToggle}
            className="form-check-input"
          />
          Cambio de frenos
        </label>
      </div>
      <div className="form-group">
        <label>
          <input
            type="checkbox"
            name="selectedServices"
            value="alineacionBalanceo"
            checked={state.selectedServices.includes('alineacionBalanceo')}
            onChange={handleServiceToggle}
            className="form-check-input"
          />
          Alineación y balanceo
        </label>
      </div>
      <div className="form-group">
        <label>
          <input
            type="checkbox"
            name="selectedServices"
            value="diagnosticoGeneral"
            checked={state.selectedServices.includes('diagnosticoGeneral')}
            onChange={handleServiceToggle}
            className="form-check-input"
          />
          Diagnóstico general
        </label>
      </div>
      <div className="form-group">
        <label>
          <input
            type="checkbox"
            name="selectedServices"
            value="revisionElectrica"
            checked={state.selectedServices.includes('revisionElectrica')}
            onChange={handleServiceToggle}
            className="form-check-input"
          />
          Revisión sistema eléctrica
        </label>
      </div>
      <div className="form-group">
        <label>
          <input
            type="checkbox"
            name="selectedServices"
            value="revisionSuspension"
            checked={state.selectedServices.includes('revisionSuspension')}
            onChange={handleServiceToggle}
            className="form-check-input"
          />
          Revisión de la suspensión
        </label>
      </div>
      <div className="form-group">
        <label htmlFor="customService">Otro servicio:</label>
        <input
          type="text"
          className="form-control"
          id="customService"
          name="customService"
          placeholder="Escribe otro servicio"
          value={customService}
          onChange={handleCustomServiceChange}
        />
        <button className="btn btn-primary mt-2" onClick={handleAddCustomService}>
          Agregar servicio personalizado
        </button>
      </div>
    </div>
  );
};

export default ServiceSelectionWindow;
