import './App.css';import React, { useState } from 'react';

const App = () => {
  const [formData, setFormData] = useState({
    clientName: '',
    email: '',
    contactNumber: '',
    taxId: '',
    identificationType: '',
    brand: '',
    model: '',
    licensePlate: '',
    fuelLevel: '',
    vehicleCondition: '',
    selectedServices: [],
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleServiceChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setFormData((prevData) => ({
        ...prevData,
        selectedServices: [...prevData.selectedServices, value],
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        selectedServices: prevData.selectedServices.filter(
          (service) => service !== value
        ),
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can perform further actions like sending the form data to an API or processing it in any desired way.
    console.log(formData);
  };

  return (
    <div>
      <h1>Propuesta - Car Shop</h1>
      <form onSubmit={handleSubmit}>
        <h2>Datos del cliente</h2>
        <input
          type="text"
          name="clientName"
          placeholder="Nombre del cliente"
          value={formData.clientName}
          onChange={handleChange}
          required
        />
        {/* Other client input fields go here */}

        <h2>Datos del vehículo</h2>
        <input
          type="text"
          name="brand"
          placeholder="Marca"
          value={formData.brand}
          onChange={handleChange}
          required
        />
        {/* Other vehicle input fields go here */}

        <h2>Selección de servicios</h2>
        <label>
          <input
            type="checkbox"
            name="selectedServices"
            value="cambioAceite"
            checked={formData.selectedServices.includes('cambioAceite')}
            onChange={handleServiceChange}
          />
          Cambio de aceite
        </label>
        {/* Other service checkboxes go here */}

        <h2>Orden de trabajo</h2>
        {/* Display the entered data for the client to review */}

        <button type="submit">Generar orden de trabajo</button>
      </form>
    </div>
  );
};

export default App;
